import React, { useState, useEffect } from 'react';
import { Container, TextField, Button, Typography, Box, IconButton, CircularProgress, Alert } from '@mui/material';
import { ArrowBack } from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import config from '../../config';

const EditFases = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const logd = useParams();
  const { id, faseId } = useParams();

  console.log(logd)
  useEffect(() => {
    const fetchFase = async () => {
      try {
        setLoading(true);
        console.log(id,faseId)
        const response = await axios.get(`${config.API_URL}/metodologia/${id}/fases/${faseId}`, {
          withCredentials: true,
        });

        setFormData(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching fase data:', error);
        setError('Error fetching fase data');
        setLoading(false);
      }
    };
    fetchFase();
  }, [id, faseId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await axios.patch(`${config.API_URL}/metodologia/${id}/fases/${faseId}`, formData, {
        withCredentials: true,
      });
      console.log('Fase actualizada:', response.data);
      setLoading(false);
      navigate(`/dashboard/methodology-management/${id}`);
    } catch (error) {
      console.error('Error actualizando fase:', error);
      setError('Error actualizando fase');
      setLoading(false);
    }
  };

  return (
    <Container>
      <Box sx={{ mt: 2, maxWidth: 600, mx: 'auto', display: 'flex', alignItems: 'center' }}>
        <IconButton onClick={() => navigate(`/dashboard/methodology-management/${id}`)}>
          <ArrowBack />
        </IconButton>
        <Typography variant="h4" sx={{ ml: 1 }}>Editar Fase</Typography>
      </Box>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2, maxWidth: 600, mx: 'auto' }}>
          {error && <Alert severity="error">{error}</Alert>}
          <TextField
            label="Nombre"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            variant="outlined"
            fullWidth
            margin="normal"
            required
          />
          <TextField
            label="DescripciÃ³n"
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            variant="outlined"
            fullWidth
            margin="normal"
            required
          />
          <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
            Guardar Cambios
          </Button>
        </Box>
      )}
    </Container>
  );
};

export default EditFases;
