import React, { useEffect, useState } from 'react';
import { Box, Toolbar, AppBar, Typography, CssBaseline, CircularProgress } from '@mui/material';
import { Outlet, Navigate, Routes, Route, useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import Cookies from 'js-cookie';
import AdminDashboard from './AdminDashboard';
import UserDashboard from './UserDashboard';

const Layout = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({ role: null, token: null });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userRole = Cookies.get('user-role');
    const jwtToken = Cookies.get('jwt');

    if (!userRole || !jwtToken) {
      navigate('/');
    } else {
      setUser({ role: userRole, token: jwtToken });
      setLoading(false);
    }
  }, [navigate]);

  if (loading) {
    return <CircularProgress />;
  }

  if (!user.role || !user.token) {
    return <Navigate to="/" />;
  }

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            GPDI : Project Management System
          </Typography>
        </Toolbar>
      </AppBar>
      <Sidebar />
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        <Routes>
          <Route path="/" element={user.role === 'admin' ? <AdminDashboard /> : <UserDashboard />} />
          {/* Añade rutas adicionales aquí */}
        </Routes>
        <Outlet />
      </Box>
    </Box>
  );
};

export default Layout;
