import React, { useEffect, useState } from 'react';
import { Box, Toolbar, AppBar, Typography, CssBaseline } from '@mui/material';
import { Outlet, Navigate, Routes, Route, useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import Cookies from 'js-cookie';
import AdminDashboard from './AdminDashboard';
import UserDashboard from './UserDashboard';

const Layout = () => {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    const cookiesApi = Cookies.get();
    const userRole = undefined
    const jwtToken = Cookies.get('jwt');

    console.log('User Role:', userRole); // Verifica que estas líneas imprimen los valores correctos
    console.log('JWT Token:', jwtToken);

    if (!userRole || !jwtToken) {
      console.log('No role or token found, navigating to login');
      navigateToLogin();
    } else {
      setRole(userRole);
      setToken(jwtToken);
    }
  }, []); // Se ejecutará solo una vez después de la primera renderización

  const navigateToLogin = () => {
    navigate('/');
  };

  if (!role || !token) {
    return null; // O un spinner/loader mientras se verifica la autenticación
  }

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            Project Management System
          </Typography>
        </Toolbar>
      </AppBar>
      <Sidebar />
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        <Routes>
          <Route path="/" element={role === 'admin' ? <AdminDashboard /> : <UserDashboard />} />
          {/* Añade rutas adicionales aquí */}
        </Routes>
        <Outlet />
      </Box>
    </Box>
  );
};

export default Layout;
