import React, { useEffect, useState } from 'react';
import { Container, Typography, Box, CircularProgress, Alert, Button, TextField, IconButton } from '@mui/material';
import { ArrowBack } from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import config from '../../config';

const GetProject = () => {
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [memberEmail, setMemberEmail] = useState('');
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const response = await axios.get(`${config.API_URL}/proyecto/${id}`, {
          withCredentials: true,
        });
        setProject(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching project:', error);
        setError('Error fetching project');
        setLoading(false);
      }
    };
    fetchProject();
  }, [id]);

  const handleAddMember = async () => {
    try {
      setLoading(true);
      const response = await axios.post(`${config.API_URL}/proyecto/${id}/addMember`, {
        email: memberEmail,
        role: 'Jefe de Proyecto'
      }, {
        withCredentials: true,
      });
      console.log('Member added:', response.data);
      setLoading(false);
      setMemberEmail('');
    } catch (error) {
      console.error('Error adding member:', error);
      setError('Error adding member');
      setLoading(false);
    }
  };

  return (
    <Container>
      <Box sx={{ mt: 2, maxWidth: 600, mx: 'auto', display: 'flex', alignItems: 'center' }}>
        <IconButton onClick={() => navigate('/dashboard/project-management')}>
          <ArrowBack />
        </IconButton>
        <Typography variant="h4" sx={{ ml: 1 }}>Configuración del Proyecto</Typography>
      </Box>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Box sx={{ mt: 2, maxWidth: 600, mx: 'auto' }}>
          {error && <Alert severity="error">{error}</Alert>}
          {project && (
            <>
              <Typography variant="h6">Nombre: {project.nombre}</Typography>
              <Typography variant="h6">Descripción: {project.descripcion}</Typography>
              <Typography variant="h6">Fecha de Inicio: {new Date(project.fechaInicio).toLocaleDateString()}</Typography>
              <Typography variant="h6">Fecha de Fin: {new Date(project.fechaFin).toLocaleDateString()}</Typography>
              <Box sx={{ mt: 2 }}>
                <TextField
                  label="Correo del Miembro"
                  value={memberEmail}
                  onChange={(e) => setMemberEmail(e.target.value)}
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />
                <Button variant="contained" color="primary" onClick={handleAddMember}>
                  Agregar Jefe de Proyecto
                </Button>
              </Box>
            </>
          )}
        </Box>
      )}
    </Container>
  );
};

export default GetProject;
