import React, { useState, useEffect } from 'react';
import { Box, List, ListItem, ListItemIcon, ListItemText, Collapse, Toolbar, Divider, Typography, Avatar, useTheme } from '@mui/material';
import { ExpandLess, ExpandMore, Settings, People, Security, SwapHoriz, AccountCircle, Dashboard } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';
import config from '../config';

const Sidebar = () => {
  const [openMantenimiento, setOpenMantenimiento] = useState(false);
  const [openProyecto, setOpenProyecto] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const [role, setRole] = useState(null);
  const theme = useTheme();

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get(`${config.API_URL}/usuario/informacion`, {
          withCredentials: true,
        });
        setUserInfo(response.data.data);
        setRole(response.data.data.role);
      } catch (error) {
        console.error('Error fetching user information:', error);
      }
    };
    fetchUserInfo();
  }, []);

  const handleToggleMantenimiento = () => {
    setOpenMantenimiento(!openMantenimiento);
  };

  const handleToggleProyecto = () => {
    setOpenProyecto(!openProyecto);
  };

  return (
    <Box sx={{ width: 280, flexShrink: 0, bgcolor: theme.palette.background.default, height: '100vh', boxShadow: theme.shadows[5], display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
      <Box>
        <Toolbar sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', pt: 2, pb: 2 }}>
          <Avatar sx={{ bgcolor: theme.palette.primary.main, width: 56, height: 56, mb: 1 }}>
            <AccountCircle fontSize="large" />
          </Avatar>
          {userInfo && (
            <>
              <Typography variant="h6" sx={{ textAlign: 'center' }}>
                {userInfo.name}
              </Typography>
              <Typography variant="body2" sx={{ color: theme.palette.text.secondary, textAlign: 'center' }}>
                {userInfo.email}
              </Typography>
              <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: theme.palette.text.secondary, textAlign: 'center' }}>
                {userInfo.role}
              </Typography>
            </>
          )}
        </Toolbar>
        <Divider />
        <List>
          <ListItem button component={Link} to="/dashboard" sx={{ '&:hover': { bgcolor: theme.palette.action.hover } }}>
            <ListItemIcon>
              <Dashboard />
            </ListItemIcon>
            <ListItemText primary="Dashboard" />
          </ListItem>
          {role === 'admin' && (
            <>
              <ListItem button onClick={handleToggleMantenimiento} sx={{ '&:hover': { bgcolor: theme.palette.action.hover } }}>
                <ListItemIcon>
                  <Settings />
                </ListItemIcon>
                <ListItemText primary="Mantenimiento" />
                {openMantenimiento ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openMantenimiento} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/user-management" sx={{ pl: 4, '&:hover': { bgcolor: theme.palette.action.hover } }}>
                    <ListItemText primary="Gestión de Usuarios" />
                  </ListItem>
                  <ListItem button component={Link} to="/methodology-management" sx={{ pl: 4, '&:hover': { bgcolor: theme.palette.action.hover } }}>
                    <ListItemText primary="Gestión de Metodologías" />
                  </ListItem>
                </List>
              </Collapse>
            </>
          )}
          <ListItem button onClick={handleToggleProyecto} sx={{ '&:hover': { bgcolor: theme.palette.action.hover } }}>
            <ListItemIcon>
              <People />
            </ListItemIcon>
            <ListItemText primary="Proyecto" />
            {openProyecto ? <ExpandLess /> : <ExpandMore />}
          </ListItem>
          <Collapse in={openProyecto} timeout="auto" unmountOnExit>
            <List component="div" disablePadding>
              <ListItem button component={Link} to="/element-management" sx={{ pl: 4, '&:hover': { bgcolor: theme.palette.action.hover } }}>
                <ListItemText primary="Gestión de Elementos" />
              </ListItem>
              <ListItem button component={Link} to="/project-management" sx={{ pl: 4, '&:hover': { bgcolor: theme.palette.action.hover } }}>
                <ListItemText primary="Gestión de Proyectos" />
              </ListItem>
            </List>
          </Collapse>
          <ListItem button component={Link} to="/security" sx={{ '&:hover': { bgcolor: theme.palette.action.hover } }}>
            <ListItemIcon>
              <Security />
            </ListItemIcon>
            <ListItemText primary="Seguridad" />
          </ListItem>
          <ListItem button component={Link} to="/change-control" sx={{ '&:hover': { bgcolor: theme.palette.action.hover } }}>
            <ListItemIcon>
              <SwapHoriz />
            </ListItemIcon>
            <ListItemText primary="Control de Cambios" />
          </ListItem>
        </List>
      </Box>
      <Box sx={{ p: 2, textAlign: 'center' }}>
        <Typography variant="body2" color="textSecondary">
          &copy; {new Date().getFullYear()} Project Management System
        </Typography>
      </Box>
    </Box>
  );
};

export default Sidebar;
