import React, { useEffect, useState } from 'react';
import { Box, Toolbar, AppBar, Typography, CssBaseline, CircularProgress, Container, Paper } from '@mui/material';
import { Outlet, Navigate, Routes, Route, useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import Cookies from 'js-cookie';
import AdminDashboard from './AdminDashboard';
import UserDashboard from './UserDashboard';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#3f51b5',
    },
    secondary: {
      main: '#f50057',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    h5: {
      fontWeight: 600,
    },
    body2: {
      color: '#3f51b5',
    },
  },
  shape: {
    borderRadius: 8,
  },
});

const Layout = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({ role: null, token: null });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userRole = Cookies.get('user-role');
    const jwtToken = Cookies.get('jwt');

    console.log('User Role:', userRole);
    console.log('JWT Token:', jwtToken);

    if (!userRole || !jwtToken) {
      console.log('No role or token found, navigating to login');
      navigate('/');
    } else {
      setUser({ role: userRole, token: jwtToken });
      setLoading(false);
    }
  }, [navigate]);

  if (loading) {
    return <CircularProgress />;
  }

  if (!user.role || !user.token) {
    return <Navigate to="/" />;
  }

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1, bgcolor: 'primary.main' }}>
          <Toolbar>
            <Typography variant="h6" noWrap component="div">
              Project Management System
            </Typography>
          </Toolbar>
        </AppBar>
        <Sidebar />
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            bgcolor: 'background.default',
            p: 3,
            minHeight: '100vh',
          }}
        >
          <Toolbar />
          <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column' }}>
              <Routes>
                <Route path="/" element={user.role === 'admin' ? <AdminDashboard /> : <UserDashboard />} />
                {/* Añade rutas adicionales aquí */}
              </Routes>
              <Outlet />
            </Paper>
          </Container>
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default Layout;
