// SelectProject.js
import React, { useEffect, useState } from 'react';
import { Container, Typography, Box, CircularProgress, Grid, Card, CardContent, Button, List, ListItem, ListItemText, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import config from '../config';

const SelectProject = () => {
  const [roles, setRoles] = useState([]);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRolesAndProjects = async () => {
      try {
        const responseRoles = await axios.get(`${config.API_URL}/roles`, { withCredentials: true });
        setRoles(responseRoles.data.data);

        const responseProjects = await axios.get(`${config.API_URL}/proyectos`, { withCredentials: true });
        setProjects(responseProjects.data.data);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching roles and projects:', error);
        setError('Error fetching roles and projects');
        setLoading(false);
      }
    };

    fetchRolesAndProjects();
  }, []);

  const handleProjectClick = (projectId) => {
    navigate(`/dashboard/project/${projectId}`);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Seleccionar Proyecto</Typography>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={3}>
          <Grid item xs={12} md={3}>
            <Paper>
              <Typography variant="h6" gutterBottom>Roles</Typography>
              <List>
                {roles.length > 0 ? (
                  roles.map((role) => (
                    <ListItem key={role._id}>
                      <ListItemText primary={role.name} />
                    </ListItem>
                  ))
                ) : (
                  <ListItem>
                    <ListItemText primary="No se encontraron roles" />
                  </ListItem>
                )}
              </List>
            </Paper>
          </Grid>
          <Grid item xs={12} md={9}>
            <Grid container spacing={3}>
              {error && <Typography color="error">{error}</Typography>}
              {projects.length > 0 ? (
                projects.map((project) => (
                  <Grid item xs={12} sm={6} md={4} key={project._id}>
                    <Card>
                      <CardContent>
                        <Typography variant="h6">{project.nombre}</Typography>
                        <Typography variant="body2">{project.descripcion}</Typography>
                        <Button
                          variant="contained"
                          color="primary"
                          sx={{ mt: 2 }}
                          onClick={() => handleProjectClick(project._id)}
                        >
                          Ver Proyecto
                        </Button>
                      </CardContent>
                    </Card>
                  </Grid>
                ))
              ) : (
                <Typography>No se encontraron proyectos</Typography>
              )}
            </Grid>
          </Grid>
        </Grid>
      )}
    </Container>
  );
};

export default SelectProject;
