import React, { useState, useEffect } from 'react';
import { Container, TextField, Button, Typography, Box, IconButton, CircularProgress, Alert } from '@mui/material';
import { ArrowBack } from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import config from '../../config';

const UserManagementEdit = () => {
  const [formData, setFormData] = useState({
    email: '',
    firtName: '',
    lastName: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${config.API_URL}/usuario/${id}`, {
          withCredentials: true,
        });


        setForData(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError('Error fetching user data');
        setLoading(false);
      }
    };
    fetchUser();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { email, ...updateData } = formData;
      setLoading(true);
      console.log("NAME : ",updateData)
      const response = await axios.patch(`${config.API_URL}/usuario/${id}`, updateData, {
        withCredentials: true,
      });
      console.log('User updated:', response.data);
      setLoading(false);
      navigate('/dashboard/user-management');
    } catch (error) {
      console.error('Error updating user:', error);
      setError('Error updating user');
      setLoading(false);
    }
  };

  return (
    <Container>
      <IconButton onClick={() => navigate('/dashboard/user-management')}>
        <ArrowBack />
      </IconButton>
      <Typography variant="h4" gutterBottom>Editar Usuario</Typography>
      {loading ? (
        <CircularProgress />
      ) : (
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
          {error && <Alert severity="error">{error}</Alert>}
          <TextField
            label="Email"
            name="email"
            value={formData.email}
            variant="outlined"
            fullWidth
            margin="normal"
            required
            disabled
          />
          <TextField
            label="First Name"
            name="firtName"
            value={formData.firtName}
            onChange={handleChange}
            variant="outlined"
            fullWidth
            margin="normal"
            required
          />
          <TextField
            label="Last Name"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            variant="outlined"
            fullWidth
            margin="normal"
            required
          />
          <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
            Guardar Cambios
          </Button>
        </Box>
      )}
    </Container>
  );
};

export default UserManagementEdit;
